  mutations: {
    setFooter(state,data){
      if(data){
        if(data.page){
          state.footerData.page=data.page;
        }
        if(data.index){
          for(var i=0;i<state.nav.length;i++){
            state.nav[i].click="";
          }
          state.nav[data.index].click="on";
        }
        if(data.childIndex){
          for(var i=0;i<state.nav.length;i++){
            if(state.nav[i].zdata){
              if(state.nav[i].zdata.length>0){
                for(var j=0;j<state.nav[i].zdata.length;j++){
