                }
              }
            }
          }
          state.nav[data.index].zdata[data.childIndex].click='on';
        }
      }
    },
    setPoetry(state,data){
      if(data){
        if(data.idx){
          for(var i=0;i<state.poetry.length;i++){
            state.poetry[i].on='';
          }
          if(data.idx<3){
            state.poetry[Number(data.idx)+1].on='on';
          }
          state.poetry[data.idx].hover='hover';
        }
      }
    },
    setLocation(state,data){
